commits.content:
@use '../../utils/mixins' as *;
@import '../../utils/vars';

.footer {
  @include flex-col;
  justify-content: flex-start;
  gap: 32px;
  padding: 0 16px;
  &__links {
    @include flex-col;
    justify-content: space-between;
    height: 65px;
    &--link {
      @include uppercase;
      color: $secondary;
    }
  }
  &__link {
    color: $secondary;
    text-decoration: none;
  }
  &__scrollup {
    margin: 0 auto;
  }
  &__text {
    @include smalltext;
    color: $secondary;
  }
}
